	        frm.set_value("total_fuel_amount",total.toFixed(2));
	        kms.push(d.unloading_km);
		});
		console.log(kms);
		kms.sort();
	    const createDifference = (kms) => {
            for(let i = 1; i < kms.length; i++){
                differenceArray.push(Math.abs(kms[i] - kms[i - 1]));
            }
            return differenceArray;

        };
        console.log(createDifference(kms));
        for(var j=0;j<differenceArray.length;j++){
            actual += differenceArray[j];
        }
        if(isNaN(actual))
        {
            actual = 0;
            frm.set_value("actual_km",actual);
            frm.refresh_field("actual_km");
        }
